void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Pixelate
    fragCoord = fragCoord - (mod(fragCoord, 10.*.5));
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;


    float antiAlias = 5./iResolution.y; // the larger the screen, the subtle is the antialias
    // If in the middle of the screen, no antialias
    float start = sin(iTime/1.333);
    float lineWidth = 0.05;
    if (uv.x > start) {
        antiAlias = 0.0;
        if (abs(uv.x - start) < lineWidth) {
            fragColor = vec4(0., 0., 1., 1.);
            return;
        }

    }

    // Output color
    vec3 c;
    // Ratio between japan red and white
    float ratio = smoothstep(0.5 - antiAlias, 0.5 + antiAlias, length(uv/1.2));
    vec3 japanRed = vec3(0.0, 0.0, 0.01);
    c = mix(
        japanRed, 
        vec3(1.),
        ratio
    );

    fragColor = vec4(
        c, 
        1
    );
}
