#define PI 3.1415926535
#define bg vec3(0.94f, 0.81f, 0.4f)
#define c1 vec3(0.84f, 0.31f, 0.42f)
#define c2 vec3(0.58f, 0.71f, 0.78f)

// from GPU gems #1 chapter 5
float stripes(float x, float f)
{
  float t = .5 + .5 * sin(f * 2. * PI * x);
  return t * t - .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ouv = uv;
    float t = iTime+1.;
    t *= 0.1;

    uv += vec2(3.0) + vec2(sin(t/2.), cos(t/2.));

    vec3 col = vec3(bg);
    float a = sin(sin(ouv.x) + cos(ouv.y*3.) + uv.x*sqrt(log(uv.y)))*uv.y;
    col = mix(c1, col, stripes(a, cos(uv.x)*10.));

    fragColor = vec4(col,1.0);
}
